mapper.update:
update t_job_task
set crontab = #{crontab,jdbcType=VARCHAR},
  trigger_url = #{triggerUrl,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, crontab, trigger_url, create_time, status
from t_job_task
where id = #{id,jdbcType=BIGINT}
